// 微信 level 应答字段列表
var WechatFields *wechatFields = func(rsps map[string][]byte) *wechatFields {
	type response struct {
		Data struct {
			ResponseList []*Field `json:"responseList"`
		} `json:"data"`
	}
	var fields = map[string][]*Field{}
	for key, bytes := range levels {
		rsp := &response{}
		err := json.Unmarshal(bytes, rsp)
		if err != nil {
			panic(err)
		}
		fields[key] = rsp.Data.ResponseList
	}
	return &wechatFields{fields: fields}
}(levels)

type wechatFields struct {
	fields map[string][]*Field
}

func (wfs *wechatFields) Get(level string) []*Field {
	return wfs.fields[level]
}

type Field struct {
	Name        string `json:"name,omitempty"`
	DisplayName string `json:"displayName"`
	Tip         string `json:"tip"`
}

type AdvertPlatform struct {
	clientID string
	secret   string
	store    AccessTokenStorage
}

func NewAdvertPlatform(clientID, secret string, store AccessTokenStorage) *AdvertPlatform {
	return &AdvertPlatform{
		clientID: clientID,
		secret:   secret,
		store:    store,
	}
}

func (ap *AdvertPlatform) AuthUrl(uri, state string) string {
	return ad.AuthRedirect(&ad.AuthRedirectOptions{
		ClientID:    ap.clientID,
		RedirectUri: uri,
		State:       state,
		Scope:       "",
		AccountType: "",
	})
}

func (ap *AdvertPlatform) AuthCode(req *http.Request) (code, state string) {
	return ad.GetAuthorizationCode(req)
}

func (ap *AdvertPlatform) SaveAccessToken(code, uri string) error {
	token, err := ad.GetAccessToken(&ad.GetAccessTokenOptions{
		ClientID:          ap.clientID,
		ClientSecret:      ap.secret,
		AuthorizationCode: code,
		RedirectUri:       uri,
	})
	if err != nil {
		return err
	}
	_, err = ap.saveToken(token)
	return err
}

func (ap *AdvertPlatform) saveToken(token *ad.AccessToken) (*AccessToken, error) {
	now := Now().Unix()
	at := &AccessToken{
		Token:           token.AccessToken,
		Refresh:         token.RefreshToken,
		TokenExpireAt:   now + (token.AccessTokenExpiresIn - (token.AccessTokenExpiresIn >> 3)),
		RefreshExpireAt: now + (token.RefreshTokenExpiresIn - (token.RefreshTokenExpiresIn >> 3)),
	}
	err := ap.store.SetAccessToken(ap.clientID+"_ad", at)
	if err != nil {
		return nil, err
	}
	return at, nil
}

func (ap *AdvertPlatform) GetAccessToken() (string, error) {
	token, err := ap.store.GetAccessToken(ap.clientID + "_ad")
	if err != nil {
		return "", err
	}
	if token == nil { // 未获得授权或刷新 token 已过期
		return "", ErrUnauthorized
	}
	now := Now().Unix()
	if token.RefreshExpireAt < now { // 刷新 token 已过期
		return "", ErrUnauthorized
	}

	if token.TokenExpireAt <= now && now < token.RefreshExpireAt { // token 已过期且仍在刷新时间内，则刷新 token
		var adToken *ad.AccessToken
		adToken, err = ad.RefreshAccessToken(&ad.RefreshAccessTokenOptions{
			ClientID:     ap.clientID,
			ClientSecret: ap.secret,
			RefreshToken: token.Refresh,
		})
		if err != nil {
			return "", err
		}
		token, err = ap.saveToken(adToken)
		if err != nil {
			return "", err
		}
	}
	return token.Token, nil
}

func (ap *AdvertPlatform) GetLevelFields(level string) []*Field {
	return WechatFields.Get(level)
}

func (ap *AdvertPlatform) GetDailyReports(opts *ad.GetDailyReportsOptions) (*ad.DailyReports, error) {
	accessToken, err := ap.GetAccessToken()
	if err != nil {
		return nil, err
	}
	return ad.GetDailyReports(accessToken, opts)
}